		CPackageValueListEditor::CPackageValueListEditor(IPackage *package) : CPackageCustomStringGrid(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Controls/ValueListEditor/ValueListEditor.h");
		}

		IControl *CPackageValueListEditor::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CValueListEditor() };
			if (properties)
				*properties = new CValueListEditorPropertyList(getPackage(), result, this);
			if (events)
				*events = new CValueListEditorEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageValueListEditor::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CValueListEditorPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageValueListEditor::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CValueListEditorEventList(getPackage(), control, this);
			return nullptr;
		}

